tple = tuple()  #tuple is immutable 

#conversion of list into tuple 
lst = [] 

for i in range(10):
    lst.append(i)
    tople = tuple(lst)
print("\n",tople)

#in tuple we can add any elements 
tple = (1,2,3,"Hello","How","are")
print("\n",tple)

#acessing tuple elements 
print(tple[2])
print(tple[-1])
print(tple[:])
print("\nReverse elements = ",tple[::-1])

#for loop in tuple 
for i in tple:
    print(i)
    
#Tuple operations 
combined_tuple = tople + tple 
print("\nCombined tuple = ",combined_tuple)

#repeat eleements in tuple 
temp = combined_tuple * 3
print("\nRepeating elements two times ",temp)

# immutable nature of tuples 
#immutable means the elements in the tuples cannot be changed 

#tuple methods 
print("\nIndex = ",combined_tuple.index("Hello"))
print("Count = ",combined_tuple.count(2))

#packing and unpacking tuple 
packed_tuple = "Hello","How","are","you","doing?"
print("\n",packed_tuple)

#unpacking a tuple 
a,b,c,d,e = packed_tuple
print("\n",a)
print(b)
print(d)
print(e)

#unpacking with *
numbers = 1,2,3,4,5,6,7,8
print(numbers)
first,*second = numbers
print(first)
print(second)

#nested list
list1 = [x for x in range(11)]
print("\nList 1 = ",list1)
list2 = [i for i in range(11) if i % 2 == 0]
print("List 2  = ",list2)
combined_list = [[i,j,k]for i in list1 for j in list2 for k in range(5)]
print("\ncombined_list",combined_list)
print(combined_list[0][0:3])

#Nested tuple
toople = ((1,2,3),(a,b,c),(4,54,6))
print("\n",toople)
print(toople[0][:3])

#nested for loop
print("\nNested for loop")
for main_topp in toople:
    for item in main_topp:
        print(item, end =" ")

